INSERT INTO `templates` (`id`, `name`, `kind`, `detail`, `rules`, `created_at`, `updated_at`)
VALUES
	(1,'service','Service','apiVersion: v1\nkind: Service\nmetadata:\n  name: {{.name}}\n  namespace: {{.namespace}}\n  annotations:\n    sidecar.istio.io/inject: \"false\"\n  labels:\n    app: {{.name}}\nspec:\n  ports: {{range $k,$v := .ports}}\n    - port: {{$v.port}}\n      name: {{$v.name}}\n      protocol: {{$v.protocol}}{{end}}\n  {{if eq .resourceType \"service\"}}selector:\n    app: {{.name}}{{end}}\n---','',NULL,'2019-07-24 14:12:34'),
	(2,'deployment','Deployment','apiVersion: apps/v1\nkind: Deployment\nmetadata:\n  name: {{.name}}\n  namespace: {{.namespace}}\n  labels:\n    app: {{.name}}\n    language: {{.language}}\nspec:\n  minReadySeconds: 10\n  replicas: {{.replicas}}\n  selector:\n    matchLabels:\n      app: {{.name}}\n  strategy:\n    rollingUpdate:\n      maxSurge: 1\n      maxUnavailable: 1\n    type: RollingUpdate\n  template:\n    metadata:\n      labels:\n        app: {{.name}}\n        language: {{.language}}\n    spec:\n      imagePullSecrets:\n      - name: regcred\n      containers:\n      - image: hub.kpaas.nsini.com/{{.image}}{{if .command}}\n        command: [{{.command}}]{{end}}{{if .args}}\n        args: [{{.args}}]{{end}}\n        imagePullPolicy: IfNotPresent\n        name: {{.name}}\n        ports: {{range $k, $v := .ports}}\n        - containerPort: {{$v.port}}{{end}}\n        resources:\n          requests:\n            memory: {{.memory}}\n          limits:\n            memory: {{.maxMemory}}\n        env:\n        - name: ENV\n          value: prod\n        - name: POD_NAME\n          valueFrom:\n            fieldRef:\n              apiVersion: v1\n              fieldPath: metadata.name\n        - name: POD_NAMESPACE\n          valueFrom:\n            fieldRef:\n              apiVersion: v1\n              fieldPath: metadata.namespace\n        - name: INSTANCE_IP\n          valueFrom:\n            fieldRef:\n              apiVersion: v1\n              fieldPath: status.podIP\n        volumeMounts:\n        - name: tz-config\n          mountPath: /etc/localtime\n      volumes:\n      - name: \"tz-config\"\n        hostPath:\n          path: \"/usr/share/zoneinfo/Asia/Shanghai\"','',NULL,'2019-08-09 16:29:48'),
	(4,'endpoints','Endpoints','apiVersion: v1\nkind: Endpoints\nmetadata:\n  name: {{.name}}\n  namespace: {{.namespace}}\nsubsets:{{range $k,$v := .addresses}}\n  - addresses:{{range $key,$val := $v.ips}}\n    - ip: {{$val}}{{end}}\n    ports:{{range $key,$val := $v.ports}}\n      - port: {{$val.port}}\n        protocol: TCP\n        name: {{$val.name}}{{end}}{{end}}','',NULL,'2018-09-21 16:49:16'),
	(5,'configmap','ConfigMap','apiVersion: v1\nkind: ConfigMap\nmetadata:\n  name: {{.name}}\n  namespace: {{.namespace}}\ndata: {{range  $k,$v := .data}} \n  -{{$v.key}} : {{$v.value}}{{end}}','',NULL,'2018-11-13 17:06:01'),
	(6,'ingress','Ingress','apiVersion: apps/v1\nkind: Ingress\nmetadata:\n  name: {{.name}}\n  namespace: {{.namespace}}\n  annotations:\n    kubernetes.io/ingress.class: \"traefik\"\nspec:\n  rules:{{range $k, $v := .rules}}\n  - host: {{$v.domain}}\n    http:\n      paths:{{range $val := $v.paths}}\n      - backend:\n          serviceName: {{$val.serviceName}}\n          servicePort: {{$val.port}}{{end}}{{end}}\n---','',NULL,'2019-06-03 15:20:24'),
	(7,'cronjob','CronJob','apiVersion: batch/v1beta1  \nkind: CronJob  \nmetadata:  \n  name: {{.name}}\n  namespace: {{.namespace}}\nspec:  \n  schedule: \"{{.schedule}}\"  \n  concurrencyPolicy: \"Replace\" # 并发性策略\n  jobTemplate:  \n    spec:  \n      template:  \n        spec:\n          restartPolicy: Never\n          imagePullSecrets:\n          - name: regcred\n          containers:  \n          - name: {{.name}}  \n            image: hub.kpaas.nsini.com/{{.image}}\n            args:  {{range $k, $v := .args}}\n            - {{$v}} {{end}}\n            env: \n            - name: ENV\n              value: test  {{range $k, $v := .envs}}\n            - name: {{$v.EnvKey}}\n              value: {{$v.EnvVar}} {{end}}{{if .logPath}}\n            volumeMounts:\n            - name: app-logs\n              mountPath: {{.logPath}}{{end}}{{if .logPath}}\n          - image: hub.kpaas.nsini.com/filebeat:6.2.4\n            name: filebeat\n            args: [\n              \"-c\", \"/etc/filebeat/filebeat.yml\",\n              \"-e\",\n            ]\n            volumeMounts:\n            - name: {{.name}}\n              mountPath: /etc/filebeat/filebeat.yml\n              readOnly: true\n              subPath: filebeat.yml\n            - name: app-logs\n              mountPath: {{.logPath}}{{end}}\n          volumes: {{if .logPath}}\n          - name: app-logs\n            emptyDir: {} {{end}}{{if .isConfigMap}}\n          - name: {{.name}}\n            configMap:\n              name: {{.name}}{{end}}\n---','',NULL,'2019-05-23 15:20:00'),
	(9,'JenkinsCommand','JenkinsCommand','APPNAME={{.app_name}}\nBUILD_PATH={{.build_path}}\nNAMESPACE={{.namespace}}\nHUB_ADDR=hub.kpaas.nsini.com\n\ndocker build -f ${BUILD_PATH}Dockerfile --rm -t ${APPNAME}:${TAGNAME} .\ndocker image tag ${APPNAME}:${TAGNAME} $HUB_ADDR/$NAMESPACE/$APPNAME:${TAGNAME}\ndocker push $HUB_ADDR/$NAMESPACE/$APPNAME:${TAGNAME}','',NULL,'2019-08-05 15:42:56'),
	(10,'ServiceEntry','ServiceEntry','apiVersion: networking.istio.io/v1alpha3\nkind: ServiceEntry\nmetadata:\n  name: {{.name}}\n  namespace: {{.namespace}}\nspec:\n  hosts: {{range $k,$v := .hosts}}\n  - {{$v}}{{end}}\n  {{if .address}}addresses: {{range $k,$v := .address}}\n  - {{$v}}{{end}}{{end}}\n  ports: {{range $k,$v := .ports}}\n  - number: {{$v.number}}\n    name: {{$v.name}}\n    protocol: {{$v.protocol}}{{end}}\n  {{if .resolution}}resolution: {{.resolution}}\n    endpoints: {{range $k, $v := .resolution.endpoints}}\n    - address: {{$v.address}}\n      ports:\n        http: {{$v.port}}\n      labels:\n        version: {{$v.version}}{{end}}{{end}}\n  {{if .resolution}}resolution: {{.resolution}}{{end}}\n---','',NULL,'2018-07-02 15:46:56'),
	(11,'Gateway','Gateway','apiVersion: networking.istio.io/v1alpha3\nkind: Gateway\nmetadata:\n  name: {{.name}}-gateway\n  namespace: {{.namespace}}\nspec:\n  selector:\n    istio: ingressgateway\n  servers: {{range $k,$v := .services}}\n  - port:\n      number: {{$v.port.number}}\n      name: {{$v.port.name}}\n      protocol: {{$v.port.number}}\n    hosts: {{range $host := $v.hosts}}\n    - \"{{$host}}\"{{end}}{{end}}','',NULL,'2019-04-08 12:23:10'),
